hudson.tasks.Shell.command:
#!/bin/bash
username=$(sed 's/,.*$//' roles/rds/files/mypasswords.txt) password=$(sed 's/^.*,//' roles/rds/files/mypasswords.txt) sqlserver=$(aws rds describe-db-instances --db-instance-identifier realsteveproddb | jq '.DBInstances[].Endpoint.Address' | sed 's/"//g')
mysql -h $sqlserver -u $username -p$password pubs \ -e "select * from authors"
